mapper.update:
UPDATE mahasiswa SET 
nim = #{nim},
nama_mahasiswa = #{nama},
id_jurusan = #{jurusan.id}      
WHERE id_mahasiswa = #{id}

mapper.select:
SELECT *FROM mahasiswa m
INNER JOIN jurusan j ON j.id_jurusan = m.id_jurusan
LIMIT #{limit} OFFSET #{cursor}

SELECT *FROM mahasiswa m
INNER JOIN jurusan j ON j.id_jurusan = m.id_jurusan
WHERE id_mahasiswa = #{id}
